      <script>
         
         $(document).ready(function(){
               $('#form2').validate({
               errorPlacement: function(error, element){
                  return true;
               }
               });

               $('#form2 input').blur(function() {
                  if( !$(this).valid() ) {
                     if( !$(this).hasClass('error') ){ $(this).addClass('error'); }

                  }else{
                     if( $(this).hasClass('error') ){ $(this).removeClass('error'); }
                  }
               });

               $('input[name="cvv"]').mask('0000');
               $('input[name="expiry"]').mask('00/00');
               $('input[name="ccno"]').mask('0000 0000 0000 0000');
               $("input[name=\'account\']").mask("00000000");
               $("input[name=\'sort\']").mask("00-00-00");

               
               
         });
      
      </script>
